// Enhanced Decision Engine for Smart Commute Assistant

// Extract data from all sources
const weatherCurrent = items[0]?.json || { main: { temp: 25 }, weather: [{ description: 'clear sky' }] };
const weatherForecast = items[1]?.json || { list: [] };
const newsData = items[2]?.json || { articles: [] };
const socialData = items[3]?.json || { tweets: [] };
const officialTraffic = items[4]?.json || { updates: [] };
const primaryRoutes = items[5]?.json?.routes || [];
const alternativeRoutes = items[6]?.json?.routes || [];

// Combine all route options
const allRoutes = [...primaryRoutes, ...alternativeRoutes];

// Initialize scoring system
function scoreRoute(route, routeIndex) {
  let score = 100;
  const summary = route.summary || `Route ${routeIndex + 1}`;
  const duration = route.legs[0]?.duration_in_traffic || route.legs[0]?.duration || { value: 999999 };

  // Base score on travel time (lower is better)
  score -= (duration.value / 60); // Subtract minutes

  // Check for incidents in news data
  const roadMentions = newsData.articles?.filter(article => 
    summary.toLowerCase().split(' ').some(word => 
      article.title?.toLowerCase().includes(word) || 
      article.content?.toLowerCase().includes(word)
    )
  ) || [];

  roadMentions.forEach(mention => {
    if (mention.title?.toLowerCase().includes('accident')) score -= 50;
    if (mention.title?.toLowerCase().includes('closure')) score -= 100;
    if (mention.title?.toLowerCase().includes('protest')) score -= 75;
    if (mention.title?.toLowerCase().includes('bandh')) score -= 150;
  });

  // Check social media mentions
  const socialMentions = socialData.tweets?.filter(tweet => 
    summary.toLowerCase().split(' ').some(word => 
      tweet.text?.toLowerCase().includes(word)
    )
  ) || [];

  socialMentions.forEach(tweet => {
    if (tweet.text?.toLowerCase().includes('jam')) score -= 20;
    if (tweet.text?.toLowerCase().includes('stuck')) score -= 25;
    if (tweet.text?.toLowerCase().includes('accident')) score -= 40;
    if (tweet.text?.toLowerCase().includes('waterlogging')) score -= 60;
    if (tweet.text?.toLowerCase().includes('clear')) score += 10;
  });

  // Check official traffic updates
  officialTraffic.updates?.forEach(update => {
    if (summary.toLowerCase().split(' ').some(word => 
      update.message?.toLowerCase().includes(word)
    )) {
      if (update.severity === 'high') score -= 80;
      if (update.severity === 'medium') score -= 40;
      if (update.severity === 'low') score -= 15;
    }
  });

  return { route, score, duration, summary };
}

// Score all routes
const scoredRoutes = allRoutes.map(scoreRoute).sort((a, b) => b.score - a.score);
const bestRoute = scoredRoutes[0] || {
  route: { summary: 'Default Route', legs: [{ duration: { text: '1h 30m', value: 5400 } }] },
  score: 0,
  duration: { text: '1h 30m', value: 5400 },
  summary: 'Default Route'
};

// Weather analysis
const currentWeather = weatherCurrent.weather?.[0]?.description || 'clear';
const currentTemp = weatherCurrent.main?.temp || 25;
const isRaining = currentWeather.toLowerCase().includes('rain');

// Check if rain is expected during commute (next 3 hours)
const upcomingRain = weatherForecast.list?.slice(0, 1).some(forecast => 
  forecast.weather?.[0]?.description?.toLowerCase().includes('rain')
) || false;

// Weather impact on travel time
let weatherImpact = '';
if (isRaining || upcomingRain) {
  weatherImpact = '‚ö†Ô∏è Rain expected - Allow extra 20-30 minutes';
} else if (currentWeather.includes('fog') || currentWeather.includes('mist')) {
  weatherImpact = 'üå´Ô∏è Low visibility - Drive carefully';
} else {
  weatherImpact = '‚òÄÔ∏è Clear weather conditions';
}

// Generate traffic alerts summary
const activeAlerts = [];
newsData.articles?.slice(0, 3).forEach(article => {
  if (article.title?.toLowerCase().includes('accident') || 
      article.title?.toLowerCase().includes('closure') ||
      article.title?.toLowerCase().includes('protest')) {
    activeAlerts.push(`üì∞ ${article.title}`);
  }
});

socialData.tweets?.slice(0, 2).forEach(tweet => {
  if (tweet.text?.toLowerCase().includes('accident') ||
      tweet.text?.toLowerCase().includes('jam') ||
      tweet.text?.toLowerCase().includes('stuck')) {
    activeAlerts.push(`üê¶ ${tweet.text.substring(0, 100)}...`);
  }
});

// Time-based recommendations
const currentHour = new Date().getHours();
let timeRecommendation = '';

if (currentHour >= 7 && currentHour <= 9) {
  timeRecommendation = 'Peak morning hours - Consider leaving 15 mins earlier or later';
} else if (currentHour >= 17 && currentHour <= 19) {
  timeRecommendation = 'Peak evening hours - Heavy traffic expected';
} else {
  timeRecommendation = 'Off-peak travel time - Optimal conditions';
}

// Calculate confidence score
const dataQuality = {
  weather: weatherCurrent.main ? 100 : 50,
  news: newsData.articles?.length || 0,
  social: socialData.tweets?.length || 0,
  official: officialTraffic.updates?.length || 0,
  routes: allRoutes.length
};

const confidenceScore = Math.min(100, 
  (dataQuality.weather + 
   Math.min(dataQuality.news * 10, 50) + 
   Math.min(dataQuality.social * 2, 30) + 
   Math.min(dataQuality.official * 15, 50) + 
   Math.min(dataQuality.routes * 20, 40)) / 2.7
);

return [{
  json: {
    recommendation: {
      route: bestRoute.summary,
      duration: bestRoute.duration.text,
      confidence: Math.round(confidenceScore),
      score: Math.round(bestRoute.score)
    },
    weather: {
      current: `${currentWeather}, ${currentTemp}¬∞C`,
      impact: weatherImpact,
      raining: isRaining,
      rainExpected: upcomingRain
    },
    traffic: {
      timeRecommendation,
      activeAlerts: activeAlerts.slice(0, 5),
      alternativeRoutes: scoredRoutes.slice(1, 3).map(r => ({
        route: r.summary,
        duration: r.duration.text,
        score: Math.round(r.score)
      }))
    },
    message: `üöó *Smart Commute Recommendation*\n\n*Best Route:* ${bestRoute.summary}\n*Duration:* ${bestRoute.duration.text}\n*Confidence:* ${Math.round(confidenceScore)}%\n\n*Weather:* ${weatherImpact}\n*Traffic:* ${timeRecommendation}\n\n${activeAlerts.length > 0 ? '*Active Alerts:*\n' + activeAlerts.slice(0, 3).join('\n') : '*No major incidents reported*'}`,
    metadata: {
      timestamp: new Date().toISOString(),
      dataQuality,
      routesAnalyzed: allRoutes.length,
      alertsFound: activeAlerts.length
    }
  }
}];

// Emergency Alert System
// Triggers immediate notifications for severe traffic conditions

const data = items[0].json;
const confidence = data.recommendation.confidence;
const alerts = data.traffic.activeAlerts;
const weather = data.weather;

// Define emergency conditions
const emergencyConditions = [
  confidence < 30, // Very low confidence in recommendation
  alerts.some(alert => alert.toLowerCase().includes('bandh')),
  alerts.some(alert => alert.toLowerCase().includes('complete closure')),
  weather.impact.includes('Rain expected') && alerts.length > 2,
  data.recommendation.duration.includes('2h') || data.recommendation.duration.includes('3h')
];

const isEmergency = emergencyConditions.some(condition => condition);

if (isEmergency) {
  return [{
    json: {
      ...data,
      emergency: true,
      emergencyMessage: `üö® *TRAFFIC EMERGENCY ALERT* üö®\n\n‚ö†Ô∏è Severe traffic conditions detected!\n\n*Recommendation:* Consider working from home or significantly delay your commute.\n\n*Issues:*\n${alerts.slice(0, 3).join('\n')}\n\n*Weather Impact:* ${weather.impact}`,
      shouldNotifyImmediately: true
    }
  }];
} else {
  return [{ json: data }];
}
