// Collect results from previous nodes
const weather = $node["Dummy Weather"].json;
const traffic = $node["Dummy Traffic News"].json;
const routes = $node["Dummy Google Maps"].json.routes;

let bestRoute = routes[0];

// Rule: avoid ORR if accident is reported
if (traffic.alerts.includes("Accident") && bestRoute.summary.includes("Outer Ring Road")) {
  bestRoute = routes[1];
}

return [{
  json: {
    message: `Good morning! Weather in Bangalore: ${weather.weather}, ${weather.temperature}.\nTraffic alert: ${traffic.alerts}.\nRecommended route: ${bestRoute.summary} (${bestRoute.duration}).`
  }
}];
